	[200002] = {["ID"] = 200002,["Text_CN"] = "你们，自认打得过我的，都站出来晾晾。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200003] = {["ID"] = 200003,["Text_CN"] = "怎么一个人影都没有，铃兰的人都变了缩头乌龟躲起来？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200004] = {["ID"] = 200004,["Text_CN"] = "喂！那边那个！你是被我吓傻了吗，怎么不说话？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200005] = {["ID"] = 200005,["Text_CN"] = "你，你在叫我？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200006] = {["ID"] = 200006,["Text_CN"] = "（看样子是个新来的……）就是你，来跟小爷打一架吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200007] = {["ID"] = 200007,["Text_CN"] = "哪里来的家伙，有那么两下子。我约了土方拍照，不陪你闹了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200008] = {["ID"] = 200008,["Text_CN"] = "哈哈，打不过就跑，还以为多大腕。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200009] = {["ID"] = 200009,["Text_CN"] = "喂！新来的！有没有到大哥这里报道啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200010] = {["ID"] = 200010,["Text_CN"] = "为什么要跟你报道，你是签到机吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200011] = {["ID"] = 200011,["Text_CN"] = "什么态度！兄弟们！让他知道知道厉害！咳咳……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200012] = {["ID"] = 200012,["Text_CN"] = "大哥！毒烟已放，你记得戴口罩啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200013] = {["ID"] = 200013,["Text_CN"] = "少废话！老子不用！咳咳……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200014] = {["ID"] = 200014,["Text_CN"] = "什么味道……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200015] = {["ID"] = 200015,["Text_CN"] = "咳咳……果然还是应该戴口罩啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200016] = {["ID"] = 200016,["Text_CN"] = "大哥！你每次都被自己的迷烟迷晕……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200017] = {["ID"] = 200017,["Text_CN"] = "你等着，我给你报仇！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200018] = {["ID"] = 200018,["Text_CN"] = "关我什么事……你都说了他是被自己迷晕的……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200019] = {["ID"] = 200019,["Text_CN"] = "能毫发无损的抵达教室，我果然没有看错人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200020] = {["ID"] = 200020,["Text_CN"] = "来，老师教你怎么上课。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200021] = {["ID"] = 200021,["Text_CN"] = "不错，这一届的新生还挺有活力的啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200022] = {["ID"] = 200022,["Text_CN"] = "317班欢迎你，我先走了，同学们好好欢迎新同学吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200023] = {["ID"] = 200023,["Text_CN"] = "喂，我说一百块钱拿来了没有，一郎？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200024] = {["ID"] = 200024,["Text_CN"] = "不要在意这种俗事，我们来谈谈梦想吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200025] = {["ID"] = 200025,["Text_CN"] = "找死！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200026] = {["ID"] = 200026,["Text_CN"] = "不准欺负我前男友！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200027] = {["ID"] = 200027,["Text_CN"] = "！！那女孩竟然独自一人冲了上去，我得赶快去帮忙！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200028] = {["ID"] = 200028,["Text_CN"] = "呦呵，喊了帮手来？给我教训他们！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200029] = {["ID"] = 200029,["Text_CN"] = "兰，*N_B同学，谢谢你们救了我！我先走了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200030] = {["ID"] = 200030,["Text_CN"] = "哼，跑得到快。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200031] = {["ID"] = 200031,["Text_CN"] = "你前男友？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200032] = {["ID"] = 200032,["Text_CN"] = "对啊！你是新来的？我这人只认拳头，你身手不错，我交你这个朋友！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200033] = {["ID"] = 200033,["Text_CN"] = "有两下子嘛……怎么样，你们当我的小弟如何？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200034] = {["ID"] = 200034,["Text_CN"] = "是他们老大，大和。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200035] = {["ID"] = 200035,["Text_CN"] = "不必跟他们废话，打就好了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200036] = {["ID"] = 200036,["Text_CN"] = "正合我意！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200037] = {["ID"] = 200037,["Text_CN"] = "哎，小柳，听说了吗，今天新入学的人，竟然打败了龙川的阿恺！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200038] = {["ID"] = 200038,["Text_CN"] = "“影子”阿恺？凭借着异常薄弱的存在感，出手变幻莫测……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200039] = {["ID"] = 200039,["Text_CN"] = "是啊……他还轻而易举的收拾了健氏兄弟和大和他们。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200040] = {["ID"] = 200040,["Text_CN"] = "不如，我们俩其中的一个去会会他？说不定他会是我们要找的那个人。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200041] = {["ID"] = 200041,["Text_CN"] = "有点意思……我在篮球馆堵他！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200042] = {["ID"] = 200042,["Text_CN"] = "什么！小柳？你输了？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200043] = {["ID"] = 200043,["Text_CN"] = "是的，没有一点悬念……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200044] = {["ID"] = 200044,["Text_CN"] = "这么厉害？看来我也应该见见他了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200045] = {["ID"] = 200045,["Text_CN"] = "怎么又来一个……让不让人打篮球了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200046] = {["ID"] = 200046,["Text_CN"] = "我跟你们宣布，这个班，是我的！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200047] = {["ID"] = 200047,["Text_CN"] = "这个班还轮不到你说话！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200048] = {["ID"] = 200048,["Text_CN"] = "吵死了！让不让人睡觉了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200049] = {["ID"] = 200049,["Text_CN"] = "睡什么睡！起来嗨！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200050] = {["ID"] = 200050,["Text_CN"] = "又是你，这次没有迷烟，我认真跟你打！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200051] = {["ID"] = 200051,["Text_CN"] = "317的*N_B，出来！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200052] = {["ID"] = 200052,["Text_CN"] = "有何贵干？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200053] = {["ID"] = 200053,["Text_CN"] = "*N_B，刚来就当了老大，不赖嘛，来吧，我们战一场！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200054] = {["ID"] = 200054,["Text_CN"] = "真胆小，还带人来。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200055] = {["ID"] = 200055,["Text_CN"] = "不是我的人。该死！不要砸坏学校的东西！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200056] = {["ID"] = 200056,["Text_CN"] = "*N_B，我们先保护学校财产，你可不要倒下了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200057] = {["ID"] = 200057,["Text_CN"] = "碍事的总算走了，怎么样？该我们了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200058] = {["ID"] = 200058,["Text_CN"] = "好眼熟的姑娘，为什么……总觉得她浑身发着光……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200059] = {["ID"] = 200059,["Text_CN"] = "那是龙川高中的薰哦。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200060] = {["ID"] = 200060,["Text_CN"] = "你什么时候出现的！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200061] = {["ID"] = 200061,["Text_CN"] = "根本就没离开过~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200062] = {["ID"] = 200062,["Text_CN"] = "你们！盯着我的女神做什么！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200063] = {["ID"] = 200063,["Text_CN"] = "你们是不是龙川高中的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200064] = {["ID"] = 200064,["Text_CN"] = "老子是凤仙的堀内！听说你这个新来的铃兰人很厉害嘛……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200065] = {["ID"] = 200065,["Text_CN"] = "什么啊，不是龙川的啊，没劲，我走了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200066] = {["ID"] = 200066,["Text_CN"] = "别想跑！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200067] = {["ID"] = 200067,["Text_CN"] = "好强的实力……小的们！给我拦住他！我要去告诉松北老大！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200068] = {["ID"] = 200068,["Text_CN"] = "……那边好像有个宝箱。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200069] = {["ID"] = 200069,["Text_CN"] = "你再犹豫的话，就被那人抢走了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200070] = {["ID"] = 200070,["Text_CN"] = "喂，前面那个，是林浩！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200071] = {["ID"] = 200071,["Text_CN"] = "谁啊？认识薰吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200072] = {["ID"] = 200072,["Text_CN"] = "林浩可是铃兰数一数二的人物！他过来了，他想干什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200073] = {["ID"] = 200073,["Text_CN"] = "喂！听说你很厉害？我们比比，怎么样？说到打架，我可是非常厉害的哦！！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200074] = {["ID"] = 200074,["Text_CN"] = "奉陪！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200075] = {["ID"] = 200075,["Text_CN"] = "好久没有遇到和我打平的人了！怎么样？我很帅吧？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200076] = {["ID"] = 200076,["Text_CN"] = "我终于等到一个你这样的人了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200077] = {["ID"] = 200077,["Text_CN"] = "什么意思？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200078] = {["ID"] = 200078,["Text_CN"] = "铃兰高手众多，但各分派系，彼此虎视眈眈，内斗不断。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200079] = {["ID"] = 200079,["Text_CN"] = "凤仙和龙川高中的人时常来挑衅，这样下去不是办法。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200080] = {["ID"] = 200080,["Text_CN"] = "你、我、小柳、林浩联手，统一铃兰，如何？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200081] = {["ID"] = 200081,["Text_CN"] = "林浩……林浩人呢？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200082] = {["ID"] = 200082,["Text_CN"] = "你终于来了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200083] = {["ID"] = 200083,["Text_CN"] = "你在这里等我？有事？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200084] = {["ID"] = 200084,["Text_CN"] = "林浩还没有出现……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200085] = {["ID"] = 200085,["Text_CN"] = "还没找到他？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200086] = {["ID"] = 200086,["Text_CN"] = "前面怎么有凤仙的人来闹事？我见过他们，走！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200087] = {["ID"] = 200087,["Text_CN"] = "这些毒蘑菇是你放的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200088] = {["ID"] = 200088,["Text_CN"] = "是的阿鲁。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200089] = {["ID"] = 200089,["Text_CN"] = "看来要教育教育你了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200090] = {["ID"] = 200090,["Text_CN"] = "这家伙绝对是喜欢我的说，真烦。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200091] = {["ID"] = 200091,["Text_CN"] = "女人和漫画都一样，要有点神秘感才吸引人的说。先走了阿鲁！ ",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200092] = {["ID"] = 200092,["Text_CN"] = "我们去楼顶天台与小柳汇合，商量对策吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200093] = {["ID"] = 200093,["Text_CN"] = "林浩失踪，凤仙又来闹事，这中间该不会有什么关系吧？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200094] = {["ID"] = 200094,["Text_CN"] = "你你你……你们！把……把……林浩，交出来！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200095] = {["ID"] = 200095,["Text_CN"] = "你说什么？怎么声音这么小，听不清楚！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200096] = {["ID"] = 200096,["Text_CN"] = "他说林浩什么的，难道林浩失踪和凤仙有关？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200097] = {["ID"] = 200097,["Text_CN"] = "算……你厉害！就算这样，我们老大也会找到林浩的！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200098] = {["ID"] = 200098,["Text_CN"] = "你们也是来找林浩的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200099] = {["ID"] = 200099,["Text_CN"] = "看来他们和林浩失踪无关……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200100] = {["ID"] = 200100,["Text_CN"] = "我们老大刚刚得到的消息，林浩的哥哥可是之前杀害前任老大隼人的人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200101] = {["ID"] = 200101,["Text_CN"] = "别以为躲起来了我们就找不到！哼！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200102] = {["ID"] = 200102,["Text_CN"] = "原来他不是磕巴啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200103] = {["ID"] = 200103,["Text_CN"] = "关于林浩的事情，我想只有大壮知道了，我们去找他。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200104] = {["ID"] = 200104,["Text_CN"] = "你好，我是来问一些关于林浩的事情……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200105] = {["ID"] = 200105,["Text_CN"] = "哦？你是林浩的朋友？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200106] = {["ID"] = 200106,["Text_CN"] = "也不算是吧……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200107] = {["ID"] = 200107,["Text_CN"] = "哦，你有烟吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200108] = {["ID"] = 200108,["Text_CN"] = "没有……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200109] = {["ID"] = 200109,["Text_CN"] = "唉，那我只好抽我自己的了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200110] = {["ID"] = 200110,["Text_CN"] = "什么啊！原来你自己有啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200111] = {["ID"] = 200111,["Text_CN"] = "哈哈哈，来吧，只有战胜我的强者，才有资格与我谈话！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200112] = {["ID"] = 200112,["Text_CN"] = "去年，铃兰和凤仙的那场战斗中，林浩的哥哥为了救他，用砖头砸死了凤仙前任头目隼人而入狱。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200113] = {["ID"] = 200113,["Text_CN"] = "自此，铃兰和凤仙势同水火。林浩家为了还债，欠了山一会许多钱，常被山一会的人追债。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200114] = {["ID"] = 200114,["Text_CN"] = "不过，最近出现许多不明团伙打听林浩的消息，不知道和林浩失踪有没有关系。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200115] = {["ID"] = 200115,["Text_CN"] = "他们有何目的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200116] = {["ID"] = 200116,["Text_CN"] = "我也不知道，你若想知道，可以去他们常去的美食街看看。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200117] = {["ID"] = 200117,["Text_CN"] = "前面几个人……似乎是那天砸317班旗的人。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200118] = {["ID"] = 200118,["Text_CN"] = "看样子并不像铃兰的人，难道是假冒的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200119] = {["ID"] = 200119,["Text_CN"] = "他们在砸车，可能是仇人的，走，去给他们捣捣乱。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200120] = {["ID"] = 200120,["Text_CN"] = "停，让他们走。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200121] = {["ID"] = 200121,["Text_CN"] = "你不是真的想放他们走吧？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200122] = {["ID"] = 200122,["Text_CN"] = "没错，暗中跟上去，看他们去见什么人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200123] = {["ID"] = 200123,["Text_CN"] = "你们，干什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200124] = {["ID"] = 200124,["Text_CN"] = "吓我一跳！你们是谁啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200125] = {["ID"] = 200125,["Text_CN"] = "是凤仙的老大松北，和他的保镖。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200126] = {["ID"] = 200126,["Text_CN"] = "是朋友，不是保镖。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200127] = {["ID"] = 200127,["Text_CN"] = "柔山啊，既然是铃兰的人，可要好好问问他们林浩的下落了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200128] = {["ID"] = 200128,["Text_CN"] = "抱歉，输了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200129] = {["ID"] = 200129,["Text_CN"] = "看各位不出狠招，应该没有敌意。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200130] = {["ID"] = 200130,["Text_CN"] = "你真聪明。没错，林浩失踪已久，我们也在寻找他的下落。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200131] = {["ID"] = 200131,["Text_CN"] = "你怀疑此事和那边的鱼骨组有关？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200132] = {["ID"] = 200132,["Text_CN"] = "鱼骨组？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200133] = {["ID"] = 200133,["Text_CN"] = "真是孤陋寡闻啊……他们是来自海边的一个民间组织，叫鱼骨组。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200134] = {["ID"] = 200134,["Text_CN"] = "里面这些都是无关紧要的人，你们听不到有用的消息。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200135] = {["ID"] = 200135,["Text_CN"] = "看样子有必要去他们老巢看看了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200136] = {["ID"] = 200136,["Text_CN"] = "那么再会，下次见面，希望仍能和你们平静的说话。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200137] = {["ID"] = 200137,["Text_CN"] = "终于上车了，好困啊，我先睡了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200138] = {["ID"] = 200138,["Text_CN"] = "恐怕不能如愿了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200139] = {["ID"] = 200139,["Text_CN"] = "这辆车被我们承包了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200140] = {["ID"] = 200140,["Text_CN"] = "说得对啊，哥哥！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200141] = {["ID"] = 200141,["Text_CN"] = "这人好厉害啊，弟弟！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200142] = {["ID"] = 200142,["Text_CN"] = "说得对啊，哥哥！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200143] = {["ID"] = 200143,["Text_CN"] = "那个人，是龙川的土方。他怎么会在这里，该不会跟林浩的事情有关吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200144] = {["ID"] = 200144,["Text_CN"] = "想这么多做什么，直接去问问不就好了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200145] = {["ID"] = 200145,["Text_CN"] = "你是龙川的土方？你认识熏吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200146] = {["ID"] = 200146,["Text_CN"] = "喂！是问林浩的事！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200147] = {["ID"] = 200147,["Text_CN"] = "哎？是你！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200148] = {["ID"] = 200148,["Text_CN"] = "好吧，你认识林浩吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200149] = {["ID"] = 200149,["Text_CN"] = "喂喂，你没看见我吗？上次在铃兰门口我们见过的！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200150] = {["ID"] = 200150,["Text_CN"] = "真无情啊……土方，这就是我跟你提过的*N_B，帮我教训这家伙！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200151] = {["ID"] = 200151,["Text_CN"] = "怎么样，土方，我说的没错吧？因为对方太难应付，我都要笑出来了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200152] = {["ID"] = 200152,["Text_CN"] = "土方，你来这里的目的是什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200153] = {["ID"] = 200153,["Text_CN"] = "又无视我……偶尔也听听前辈的话吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200154] = {["ID"] = 200154,["Text_CN"] = "林浩不是被抓走了，是他自己去鱼骨组找人了。现在他去了风铃森林。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200155] = {["ID"] = 200155,["Text_CN"] = "听语气，你是林浩的朋友？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200156] = {["ID"] = 200156,["Text_CN"] = "打过几次交道而已。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200157] = {["ID"] = 200157,["Text_CN"] = "既不是敌人，你无缘无故打我们干什么。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200158] = {["ID"] = 200158,["Text_CN"] = "唔，听阿恺说你很厉害，想比比而已。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200159] = {["ID"] = 200159,["Text_CN"] = "想比武的话提前说一声会死吗！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200160] = {["ID"] = 200160,["Text_CN"] = "土方别聊了，我们去拍大海吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200161] = {["ID"] = 200161,["Text_CN"] = "走吧，先说好，不准再偷拍我。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200162] = {["ID"] = 200162,["Text_CN"] = "鱼骨组的人在那边鬼鬼祟祟的搬一个木桶。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200163] = {["ID"] = 200163,["Text_CN"] = "走，去看看。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200164] = {["ID"] = 200164,["Text_CN"] = "这座山是我们的！谁也别想破坏它！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200165] = {["ID"] = 200165,["Text_CN"] = "你们为何要攻击这个木桶？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200166] = {["ID"] = 200166,["Text_CN"] = "这炸药桶不是你们运来的吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200167] = {["ID"] = 200167,["Text_CN"] = "这桶里竟然是炸药！？是鱼骨组的人运来的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200168] = {["ID"] = 200168,["Text_CN"] = "对不起，误会了你们。鱼骨组的第二分队队长凛之助前阵子来了山里，似乎在找什么东西。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200169] = {["ID"] = 200169,["Text_CN"] = "前几日就运了一桶炸药进来，炸伤很多山民。所以山民们才会疯狂的想把炸药箱毁掉。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200170] = {["ID"] = 200170,["Text_CN"] = "鱼骨组实在可恶……那么，你可见过林浩？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200171] = {["ID"] = 200171,["Text_CN"] = "那个金发怪人？他来找过凛之助。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200172] = {["ID"] = 200172,["Text_CN"] = "只是不巧，在林浩来之前不久，凛之助刚刚离开，于是林浩也离开了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200173] = {["ID"] = 200173,["Text_CN"] = "谢谢你的消息，那么我们先走了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200174] = {["ID"] = 200174,["Text_CN"] = "老大！那个林浩去了咱们在东京的秘密据点“水门”！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200175] = {["ID"] = 200175,["Text_CN"] = "什么！放下手中的所有事情，即刻赶往“水门”！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200176] = {["ID"] = 200176,["Text_CN"] = "什么人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200177] = {["ID"] = 200177,["Text_CN"] = "啊呀，还是被发现了啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200178] = {["ID"] = 200178,["Text_CN"] = "喂，那人怎么一动不动了，不会是我们出手太重给打死了吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200179] = {["ID"] = 200179,["Text_CN"] = "还真是和那个林浩的哥哥一样蠢啊，分不清是晕了还是死了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200180] = {["ID"] = 200180,["Text_CN"] = "什么意思？！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200181] = {["ID"] = 200181,["Text_CN"] = "啊哈哈，我什么都没说过。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200182] = {["ID"] = 200182,["Text_CN"] = "看样子他知道当年的事情！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200183] = {["ID"] = 200183,["Text_CN"] = "好了好了别打了！我说还不行吗！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200184] = {["ID"] = 200184,["Text_CN"] = "去年，铃兰和凤仙有交好倾向。我们鱼骨组怕两强联合后无法抗衡，便制造矛盾挑起了铃兰和凤仙的战争。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200185] = {["ID"] = 200185,["Text_CN"] = "而在那次战斗中，林浩的哥哥只是砸晕了隼人，是我们机智的二队长凛之助暗中砸死了隼人。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200186] = {["ID"] = 200186,["Text_CN"] = "自此，铃兰和凤仙势不两立。因为此事，凛之助还荣升了第二分队队长。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200187] = {["ID"] = 200187,["Text_CN"] = "林浩一定是知道了真相，不想连累众人，独自闯入鱼骨组寻找凛之助。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200188] = {["ID"] = 200188,["Text_CN"] = "这家伙一向如此啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200189] = {["ID"] = 200189,["Text_CN"] = "……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200190] = {["ID"] = 200190,["Text_CN"] = "走，去东京。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200191] = {["ID"] = 200191,["Text_CN"] = "我脸上写着铃兰吗？怎么一下车就被包围了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200192] = {["ID"] = 200192,["Text_CN"] = "他们看样子不是鱼骨组的人，只是当地帮派例行打压外地帮派而已。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200193] = {["ID"] = 200193,["Text_CN"] = "是你，你和那个小王爷是一伙的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200194] = {["ID"] = 200194,["Text_CN"] = "别拿我和那些乡下人相提并论，我可是不折不扣的都市妹阿鲁。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200195] = {["ID"] = 200195,["Text_CN"] = "当心那些香蕉皮！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200196] = {["ID"] = 200196,["Text_CN"] = "你们离我这么近，要对我做什么阿鲁！要非礼吗阿鲁！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200197] = {["ID"] = 200197,["Text_CN"] = "三人都掉进下水道去了，恶作剧，大成功！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200198] = {["ID"] = 200198,["Text_CN"] = "熬夜是Lady的天敌阿鲁，我要回去睡觉了，再会啦。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200199] = {["ID"] = 200199,["Text_CN"] = "可恶，被一个小丫头耍了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200200] = {["ID"] = 200200,["Text_CN"] = "你知足吧，以前我们被耍的更惨。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200201] = {["ID"] = 200201,["Text_CN"] = "哎？快讲讲！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200202] = {["ID"] = 200202,["Text_CN"] = "少废话，快点离开这里吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200203] = {["ID"] = 200203,["Text_CN"] = "真小气，小柳，你讲讲呗。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200204] = {["ID"] = 200204,["Text_CN"] = "快走。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200205] = {["ID"] = 200205,["Text_CN"] = "哼，别以为我不知道，你装出一副冷漠的样子，一定是为了吸引小姑娘。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200206] = {["ID"] = 200206,["Text_CN"] = "！！龙川高中的老大， 秀一！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200207] = {["ID"] = 200207,["Text_CN"] = "他怎么会在这里？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200208] = {["ID"] = 200208,["Text_CN"] = "他是东京人，可能回家办事。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200209] = {["ID"] = 200209,["Text_CN"] = "喂喂，你是东京人，你知道“水门”在哪吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200210] = {["ID"] = 200210,["Text_CN"] = "太冷漠了！比小柳还冷漠！喂！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200211] = {["ID"] = 200211,["Text_CN"] = "哇，是你！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200212] = {["ID"] = 200212,["Text_CN"] = "秀一，我们一起对付这些人。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200213] = {["ID"] = 200213,["Text_CN"] = "喂喂，这个薰，似乎暗恋着秀一啊，你看她那眼神。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200214] = {["ID"] = 200214,["Text_CN"] = "我也看出来了……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200215] = {["ID"] = 200215,["Text_CN"] = "你就是铃兰的*N_B？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200216] = {["ID"] = 200216,["Text_CN"] = "就是我，怎样！再打一局分个胜负吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200217] = {["ID"] = 200217,["Text_CN"] = "我看我们没必要打，你们应该更在意林浩的事情吧。“水门”就在前面的山上。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200218] = {["ID"] = 200218,["Text_CN"] = "下次再打！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200219] = {["ID"] = 200219,["Text_CN"] = "铃兰那些人怎么也不会知道，这里只是幌子，真正的“水门”是在下水道，哈哈哈哈哈！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200220] = {["ID"] = 200220,["Text_CN"] = "抱歉，我现在知道了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200221] = {["ID"] = 200221,["Text_CN"] = "什么！？揍他！否则队长不会饶了我们！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200222] = {["ID"] = 200222,["Text_CN"] = "你们还是找来了啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200223] = {["ID"] = 200223,["Text_CN"] = "喂，不说一声就走，大家还以为你出事了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200224] = {["ID"] = 200224,["Text_CN"] = "哈哈哈，我可是非常强大的！怎么会出事！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200225] = {["ID"] = 200225,["Text_CN"] = "凛之助找到了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200226] = {["ID"] = 200226,["Text_CN"] = "刚刚得到消息，他准备离开东京！我正要去火车站！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200227] = {["ID"] = 200227,["Text_CN"] = "快走，别让他逃了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200228] = {["ID"] = 200228,["Text_CN"] = "你去自首吧，恩怨就此了结。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200229] = {["ID"] = 200229,["Text_CN"] = "好……好的！我一定去！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200230] = {["ID"] = 200230,["Text_CN"] = "什么情况？咱们学校这里怎么有这么多凤仙的人？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200231] = {["ID"] = 200231,["Text_CN"] = "这里已经是凤仙的地盘了！还不快叫大哥！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200232] = {["ID"] = 200232,["Text_CN"] = "活腻了吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200233] = {["ID"] = 200233,["Text_CN"] = "校门口那些废柴，真是拿回家都烧不起来阿鲁！让你闯进来了阿鲁！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200234] = {["ID"] = 200234,["Text_CN"] = "那些人怎么可能拦得住我？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200235] = {["ID"] = 200235,["Text_CN"] = "但是你的步伐也要到此为止了阿鲁！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200236] = {["ID"] = 200236,["Text_CN"] = "地上这些水是你弄的？可恶，不要跑！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200237] = {["ID"] = 200237,["Text_CN"] = "你是宫野兰的前男友？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200238] = {["ID"] = 200238,["Text_CN"] = "你你你跟我说话客气点！我现在可是凤仙高中钦点代管铃兰的人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200239] = {["ID"] = 200239,["Text_CN"] = "原来投靠了凤仙啊，哼。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200240] = {["ID"] = 200240,["Text_CN"] = "你别嚣张！我们人多！现在可不一样了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200241] = {["ID"] = 200241,["Text_CN"] = "啊啊啊别打了！我告诉你一个重要情报！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200242] = {["ID"] = 200242,["Text_CN"] = "说！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200243] = {["ID"] = 200243,["Text_CN"] = "凤仙老大松北正带人开车而来，准备带大批人进入铃兰！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200244] = {["ID"] = 200244,["Text_CN"] = "你快想办法呀！武田卫带了一队人在咱们楼顶等着接应呢！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200245] = {["ID"] = 200245,["Text_CN"] = "幸野，你带人去布置路障，我去楼顶对付武田卫他们。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200246] = {["ID"] = 200246,["Text_CN"] = "铃兰的大门，你们进的来，不一定出的去！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200247] = {["ID"] = 200247,["Text_CN"] = "啊！是他！我得了一种见到他就会死的病！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200248] = {["ID"] = 200248,["Text_CN"] = "废物！躲什么！上去啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200249] = {["ID"] = 200249,["Text_CN"] = "不打了，看时间，老大松北该到了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200250] = {["ID"] = 200250,["Text_CN"] = "糟了，得快去学校门口看看！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200251] = {["ID"] = 200251,["Text_CN"] = "情况如何了？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200252] = {["ID"] = 200252,["Text_CN"] = "凤仙果然开了车过来，他们正在损坏我们布置的路障！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200253] = {["ID"] = 200253,["Text_CN"] = "不能让他们得逞，保护路障，阻止凤仙的车辆进入铃兰！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200254] = {["ID"] = 200254,["Text_CN"] = "算你，厉害。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200255] = {["ID"] = 200255,["Text_CN"] = "你们老大呢？你不是形影不离的跟着他吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200256] = {["ID"] = 200256,["Text_CN"] = "不说。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200257] = {["ID"] = 200257,["Text_CN"] = "刚才有人看见松北领着一队人去了篮球馆，这里有我，你快去看看吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200258] = {["ID"] = 200258,["Text_CN"] = "又见面了。你上次说希望下次见面时能和平的谈话，现在看来要让你失望了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200259] = {["ID"] = 200259,["Text_CN"] = "上次见到你，我就料到会有与你对立的一天。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200260] = {["ID"] = 200260,["Text_CN"] = "立场不同，各自为战。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200261] = {["ID"] = 200261,["Text_CN"] = "凤仙的老大，我也想看看你的本事！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200262] = {["ID"] = 200262,["Text_CN"] = "停！你们这样会打个三天三夜的！明明就分不出胜负，还打什么？！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200263] = {["ID"] = 200263,["Text_CN"] = "*N_B，为什么不告诉松北隼人之死的真相。 ",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200264] = {["ID"] = 200264,["Text_CN"] = "隼人大哥不是林浩的哥哥打死的吗？正因为如此我们才要占领铃兰，为大哥报仇。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200265] = {["ID"] = 200265,["Text_CN"] = "是鱼骨组设计陷害，真凶是鱼骨组的凛之助。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200266] = {["ID"] = 200266,["Text_CN"] = "不早说，浪费力气。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200267] = {["ID"] = 200267,["Text_CN"] = "哈哈，想和你比试比试嘛。你那么聪明，我要是提个一句半句，我们还打得起来吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200268] = {["ID"] = 200268,["Text_CN"] = "……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200269] = {["ID"] = 200269,["Text_CN"] = "凤仙对铃兰不再有仇恨，我们会撤出铃兰。*N_B，期待下次见面。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200270] = {["ID"] = 200270,["Text_CN"] = "你看到了吧，论实力，铃兰与凤仙不相上下，却因为凤仙的团结，铃兰险些被凤仙占领。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200271] = {["ID"] = 200271,["Text_CN"] = "嗯……你说的是对的……统一铃兰势在必行。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200272] = {["ID"] = 200272,["Text_CN"] = "先统一一年级。大和他们正好在那边，就从他们开始吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200273] = {["ID"] = 200273,["Text_CN"] = "好。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200274] = {["ID"] = 200274,["Text_CN"] = "你想要统一铃兰一年级？真是伟大的志向啊！先让老师看看，老师的试炼可是很严格的哦！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200275] = {["ID"] = 200275,["Text_CN"] = "尽管来吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200276] = {["ID"] = 200276,["Text_CN"] = "看样子你不是夸下海口啊。不错，你去吧，我就当什么都没看见。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200277] = {["ID"] = 200277,["Text_CN"] = "你是来看演唱会的？买票了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200278] = {["ID"] = 200278,["Text_CN"] = "什么演唱会……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200279] = {["ID"] = 200279,["Text_CN"] = "我们老大奇拉的演唱会啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200280] = {["ID"] = 200280,["Text_CN"] = "看来你已经投靠了奇拉啊，正好，一起收拾了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200281] = {["ID"] = 200281,["Text_CN"] = "呦呦呦！有人来看演唱会了！快过来，我给你签个名！别害羞，签在衣服上好不好？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200282] = {["ID"] = 200282,["Text_CN"] = "啊~我们，来场华丽的表演吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200283] = {["ID"] = 200283,["Text_CN"] = "哈哈哈，你终于答应了幸野的请求？这事可是很麻烦的！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200284] = {["ID"] = 200284,["Text_CN"] = "是啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200285] = {["ID"] = 200285,["Text_CN"] = "我们的比试不能那么无聊，我们比守旗如何？这樱花街的2面旗子，谁能守得住，谁就赢了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200286] = {["ID"] = 200286,["Text_CN"] = "算你赢了吧，刚才我用力过猛，把我那边的旗子打断了……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200287] = {["ID"] = 200287,["Text_CN"] = "哈哈哈，怪不得你来给我捣乱了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200288] = {["ID"] = 200288,["Text_CN"] = "废弃",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200289] = {["ID"] = 200289,["Text_CN"] = "哈哈哈，行吧，反正铃兰那破事我也不想管。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200290] = {["ID"] = 200290,["Text_CN"] = "我们哥俩跟你来一场，如何？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200291] = {["ID"] = 200291,["Text_CN"] = "你们两人一齐上阵，那可是不容小觑的啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200292] = {["ID"] = 200292,["Text_CN"] = "来吧，练练！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200293] = {["ID"] = 200293,["Text_CN"] = "暗恋你的女生那么多，我要是打伤你，不会被追杀吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200294] = {["ID"] = 200294,["Text_CN"] = "不负众望啊，老大。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200295] = {["ID"] = 200295,["Text_CN"] = "你呢，小柳。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200296] = {["ID"] = 200296,["Text_CN"] = "老大。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200297] = {["ID"] = 200297,["Text_CN"] = "哈哈哈哈！当老大竟然这么爽！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200298] = {["ID"] = 200298,["Text_CN"] = "放假了，还真想念山里的青木啊，她在山里那么无聊，我要邀请她来铃兰上学！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200299] = {["ID"] = 200299,["Text_CN"] = "弟弟，又是他！这次不能放过！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200300] = {["ID"] = 200300,["Text_CN"] = "说得对啊哥哥！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200301] = {["ID"] = 200301,["Text_CN"] = "听说鱼骨组第二队整队砸在了你手里？一群废物。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200302] = {["ID"] = 200302,["Text_CN"] = "你谁啊？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200303] = {["ID"] = 200303,["Text_CN"] = "我是鱼骨组第三队队长，我们的实力，比凛之助那群草包强多了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200304] = {["ID"] = 200304,["Text_CN"] = "哼，强大之处就是用毒吗！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200305] = {["ID"] = 200305,["Text_CN"] = "少废话！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200306] = {["ID"] = 200306,["Text_CN"] = "你们三个怎么会在一起？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200307] = {["ID"] = 200307,["Text_CN"] = "喂喂喂，你们三个，不认识我了吗！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200308] = {["ID"] = 200308,["Text_CN"] = "这三人怎么举止如此怪异？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200309] = {["ID"] = 200309,["Text_CN"] = "莫非中了鱼骨组的毒，要赶快叫醒他们！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200310] = {["ID"] = 200310,["Text_CN"] = "趁着他们三人晕了，得赶紧从鱼骨组手中拿到解药！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200311] = {["ID"] = 200311,["Text_CN"] = "看样子这就是解药了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200312] = {["ID"] = 200312,["Text_CN"] = "啊……发生了什么。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200313] = {["ID"] = 200313,["Text_CN"] = "你们刚刚中了鱼骨组的毒。说来，你怎么会和他们两人在一起？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200314] = {["ID"] = 200314,["Text_CN"] = "他们是来接我入学龙川高中的，我已经答应了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200315] = {["ID"] = 200315,["Text_CN"] = "怎么觉得身上好疼，土方，你对我做过什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200316] = {["ID"] = 200316,["Text_CN"] = "少废话。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200317] = {["ID"] = 200317,["Text_CN"] = "……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200318] = {["ID"] = 200318,["Text_CN"] = "好吧，既然你已经答应了他们，祝你入学顺利，青木。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200319] = {["ID"] = 200319,["Text_CN"] = "该死！鱼骨组的人在砸火车头，想阻止我离开吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200320] = {["ID"] = 200320,["Text_CN"] = "看样子给他们的教训还不够！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200321] = {["ID"] = 200321,["Text_CN"] = "我眼花了吗，怎么看到一个黑影鬼鬼祟祟的闪过。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200322] = {["ID"] = 200322,["Text_CN"] = "……我好像被包围了，看样子是没看错。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200323] = {["ID"] = 200323,["Text_CN"] = "果然在这里！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200324] = {["ID"] = 200324,["Text_CN"] = "什么人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200325] = {["ID"] = 200325,["Text_CN"] = "鬼鬼祟祟的，我倒要问你是什么人。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200326] = {["ID"] = 200326,["Text_CN"] = "既然你撞见了，就不能留你了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200327] = {["ID"] = 200327,["Text_CN"] = "切，让他跑了……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200328] = {["ID"] = 200328,["Text_CN"] = "鱼骨组的人在翻垃圾桶，走，去看看里面有什么。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200329] = {["ID"] = 200329,["Text_CN"] = "这是……枪！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200330] = {["ID"] = 200330,["Text_CN"] = "林浩该不会有什么危险吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200331] = {["ID"] = 200331,["Text_CN"] = "快走！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200332] = {["ID"] = 200332,["Text_CN"] = "啊，好累，必须要吃一碗美美的拉面！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200333] = {["ID"] = 200333,["Text_CN"] = "谁也不准进去！要去就去隔壁，我家的烧烤比这家拉面好吃多了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200334] = {["ID"] = 200334,["Text_CN"] = "谁也别想阻止我吃拉面！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200335] = {["ID"] = 200335,["Text_CN"] = "吵死了！在里面喝个酒都不得安生！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200336] = {["ID"] = 200336,["Text_CN"] = "刚才是你在往外面扔杯子？差点砸到我！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200337] = {["ID"] = 200337,["Text_CN"] = "是你先打扰我喝酒的！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200338] = {["ID"] = 200338,["Text_CN"] = "看样子要用拳头说话了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200339] = {["ID"] = 200339,["Text_CN"] = "可恶，把酒弄的满地都是啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200340] = {["ID"] = 200340,["Text_CN"] = "哼，平手。无聊，我要回去睡觉了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200341] = {["ID"] = 200341,["Text_CN"] = "喂……出口在那边……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200342] = {["ID"] = 200342,["Text_CN"] = "……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200343] = {["ID"] = 200343,["Text_CN"] = "宫野兰，你怎么在这里？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200344] = {["ID"] = 200344,["Text_CN"] = "我跟我新男友出来压马路啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200345] = {["ID"] = 200345,["Text_CN"] = "你们竟然在一起了？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200346] = {["ID"] = 200346,["Text_CN"] = "是的，我们刚旅行回来。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200347] = {["ID"] = 200347,["Text_CN"] = "一阵子没去学校，你竟然成为了一年级老大？我现在可是全身橙装！我不服！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200348] = {["ID"] = 200348,["Text_CN"] = "好吧，你赢了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200349] = {["ID"] = 200349,["Text_CN"] = "不不，你也很厉害，当她的男朋友才是最难的挑战啊！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200350] = {["ID"] = 200350,["Text_CN"] = "祝你好运。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200351] = {["ID"] = 200351,["Text_CN"] = "！！那几个人，是火车上与神秘男人接头的人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200352] = {["ID"] = 200352,["Text_CN"] = "他们在砸谁的车？这就是他们在火车顶商量的阴谋吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200353] = {["ID"] = 200353,["Text_CN"] = "不管了，先阻止他们再说。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200354] = {["ID"] = 200354,["Text_CN"] = "求放过！我们也是受人指使的！他说了，只要到处捣乱，就有大把的钱拿。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200355] = {["ID"] = 200355,["Text_CN"] = "是火车顶上跟你秘密会面的人？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200356] = {["ID"] = 200356,["Text_CN"] = "对，但我不知道他的来历，看起来是个贵族，很有钱的样子。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200357] = {["ID"] = 200357,["Text_CN"] = "他的目的是什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200358] = {["ID"] = 200358,["Text_CN"] = "这我也不知道……不过，约好今晚我去城北那个荒僻的山顶领取报酬。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200359] = {["ID"] = 200359,["Text_CN"] = "你可以去找找，他应该会在那里。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200360] = {["ID"] = 200360,["Text_CN"] = "拿着，这是你的报酬。真没想到一介平民也能体会贵族的感受啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200361] = {["ID"] = 200361,["Text_CN"] = "你联系黑道制造混乱，究竟有什么目的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200362] = {["ID"] = 200362,["Text_CN"] = "啊呀，你想破坏我的计划么？那可别怪我手下不留情了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200363] = {["ID"] = 200363,["Text_CN"] = "安心去吧，我会给你挑块好墓地。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200364] = {["ID"] = 200364,["Text_CN"] = "你是铃兰的？没想到，铃兰这一届的新生有如此实力……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200365] = {["ID"] = 200365,["Text_CN"] = "现在你可以告诉我你的目的了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200366] = {["ID"] = 200366,["Text_CN"] = "告诉你也无妨。我心爱的猎狗被山一会的伊凡开车压死了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200367] = {["ID"] = 200367,["Text_CN"] = "他的车我已经派人去砸了，只是伊凡躲着不出来。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200368] = {["ID"] = 200368,["Text_CN"] = "而我重金找人制造混乱，就是为了引出山一会。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[200369] = {["ID"] = 200369,["Text_CN"] = "山一会啊，总找林浩麻烦的那个组织，敌人的敌人就是朋友，我来帮你完成计划。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300001] = {["ID"] = 300001,["Text_CN"] = "我们班的小柳，躲在篮球场的更衣室里哭哭啼啼，你们到底发生了什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300002] = {["ID"] = 300002,["Text_CN"] = "我们之间发生的，那可有点多，老师你想听哪一种？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300003] = {["ID"] = 300003,["Text_CN"] = "原来如此，你赢了小柳，很快就会名声大震的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300004] = {["ID"] = 300004,["Text_CN"] = "恭喜你，完完整整的融入了这里……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300005] = {["ID"] = 300005,["Text_CN"] = "你以为篮球场的风波已经结束了？不，只是战争的开始。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300006] = {["ID"] = 300006,["Text_CN"] = "他有一个官方CP，只怕这时候已经在篮球场等你了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300007] = {["ID"] = 300007,["Text_CN"] = "我不管，我要打篮球。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300008] = {["ID"] = 300008,["Text_CN"] = "有两下子。（得赶快联系林浩，我有预感，这个人就是我一直在等的人。）",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300009] = {["ID"] = 300009,["Text_CN"] = "你还有事吗，我要去吃午饭了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300010] = {["ID"] = 300010,["Text_CN"] = "你要去吃午饭？大和他们也去了，他们和你有仇，你可小心些。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300011] = {["ID"] = 300011,["Text_CN"] = "我还会怕他们不成？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300012] = {["ID"] = 300012,["Text_CN"] = "果然遇上了吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300013] = {["ID"] = 300013,["Text_CN"] = "不是“遇”，是他们主动来送死的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300014] = {["ID"] = 300014,["Text_CN"] = "饿死了，我先去吃饭了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300015] = {["ID"] = 300015,["Text_CN"] = "你们这梁子算是结下了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300016] = {["ID"] = 300016,["Text_CN"] = "你们317班正在争老大之位呢，你不回去看看？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300017] = {["ID"] = 300017,["Text_CN"] = "我还用跟他们争吗？那群战五渣……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300018] = {["ID"] = 300018,["Text_CN"] = "好累，我回班级睡觉去，再见了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300019] = {["ID"] = 300019,["Text_CN"] = "什么情况，317怎么这么乱？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300020] = {["ID"] = 300020,["Text_CN"] = "我只想睡午觉而已，不小心卷入了317之王的争夺战。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300021] = {["ID"] = 300021,["Text_CN"] = "哦呵呵~这样啊，老师看好你，加油哦！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300022] = {["ID"] = 300022,["Text_CN"] = "我就知道你可以的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300023] = {["ID"] = 300023,["Text_CN"] = "你成为317之王的事情已经传开了，316班的老大原叶似乎想找你挑战！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300024] = {["ID"] = 300024,["Text_CN"] = "尽管来吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300025] = {["ID"] = 300025,["Text_CN"] = "不赖嘛，317之王。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300026] = {["ID"] = 300026,["Text_CN"] = "喏，砸旗的那些人都冲着你来了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300027] = {["ID"] = 300027,["Text_CN"] = "为什么不找你啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300028] = {["ID"] = 300028,["Text_CN"] = "这些人为什么无端要毁坏校舍？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300029] = {["ID"] = 300029,["Text_CN"] = "算了，快点赶走这些人，我们正正经经的打一架吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300030] = {["ID"] = 300030,["Text_CN"] = "这次算你赢。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300031] = {["ID"] = 300031,["Text_CN"] = "什么叫算我赢，本来就是我赢了啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300032] = {["ID"] = 300032,["Text_CN"] = "铃~~~放学了，你还不回家？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300033] = {["ID"] = 300033,["Text_CN"] = "……再见！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300034] = {["ID"] = 300034,["Text_CN"] = "喂，你看上薰了？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300035] = {["ID"] = 300035,["Text_CN"] = "要你管！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300036] = {["ID"] = 300036,["Text_CN"] = "那个白树可是薰的头号粉丝，看，他喊人来对付你了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300037] = {["ID"] = 300037,["Text_CN"] = "你还真是喜欢四处惹事啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300038] = {["ID"] = 300038,["Text_CN"] = "喂喂，被人围住了啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300039] = {["ID"] = 300039,["Text_CN"] = "莫非他们也是龙川高中的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300040] = {["ID"] = 300040,["Text_CN"] = "看样子不像龙川高中的啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300041] = {["ID"] = 300041,["Text_CN"] = "他们是冲着你来的，你去问问吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300042] = {["ID"] = 300042,["Text_CN"] = "没想到你的名声已经传到凤仙高中去了……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300043] = {["ID"] = 300043,["Text_CN"] = "这武田卫真没品啊，自己跑了，留下一群小弟当挡箭牌。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300044] = {["ID"] = 300044,["Text_CN"] = "这些人可真缠人，快点了结他们。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300045] = {["ID"] = 300045,["Text_CN"] = "他们可算是走了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300046] = {["ID"] = 300046,["Text_CN"] = "我终于可以回家了，你还要跟着我吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300047] = {["ID"] = 300047,["Text_CN"] = "只是碰巧，我回家也走这条路而已。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300048] = {["ID"] = 300048,["Text_CN"] = "赶快打开宝箱看看有什么宝贝吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300049] = {["ID"] = 300049,["Text_CN"] = "是金币啊，你命不错。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300050] = {["ID"] = 300050,["Text_CN"] = "走，请你喝酒。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300051] = {["ID"] = 300051,["Text_CN"] = "这些老弱病残还想抢宝箱，他们是来搞笑的吗。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300052] = {["ID"] = 300052,["Text_CN"] = "多谢你请我喝酒。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300053] = {["ID"] = 300053,["Text_CN"] = "甭客气。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300054] = {["ID"] = 300054,["Text_CN"] = "喂，前面那个，是林浩！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300055] = {["ID"] = 300055,["Text_CN"] = "他认识薰吗？我去看看！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300056] = {["ID"] = 300056,["Text_CN"] = "林浩这人向来脱线，可能他自己走了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300057] = {["ID"] = 300057,["Text_CN"] = "哦，没什么事我先回家了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300058] = {["ID"] = 300058,["Text_CN"] = "你来了，幸野在校门口等你。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300059] = {["ID"] = 300059,["Text_CN"] = "好，我知道了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300060] = {["ID"] = 300060,["Text_CN"] = "怎么这么多凤仙的人来闹事？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300061] = {["ID"] = 300061,["Text_CN"] = "喂喂，是凤仙的桃子。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300062] = {["ID"] = 300062,["Text_CN"] = "怎么？她很厉害？看起来不像啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300063] = {["ID"] = 300063,["Text_CN"] = "她可是恶作剧女王，对付她可要千万小心！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300064] = {["ID"] = 300064,["Text_CN"] = "这祖宗可算是走了……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300065] = {["ID"] = 300065,["Text_CN"] = "你好像很怕桃子嘛，是不是被她欺负过？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300066] = {["ID"] = 300066,["Text_CN"] = "别在这里聊了，我们去楼顶与小柳汇合，商议如何应对凤仙吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300067] = {["ID"] = 300067,["Text_CN"] = "这堀内一向怯懦，说话声音太小，只听到林浩什么的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300068] = {["ID"] = 300068,["Text_CN"] = "难道林浩失踪和凤仙有关？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300069] = {["ID"] = 300069,["Text_CN"] = "堀内一直躲在那些人后面，抓住他问个清楚吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300070] = {["ID"] = 300070,["Text_CN"] = "关于林浩的事情，我想只有大壮知道了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300071] = {["ID"] = 300071,["Text_CN"] = "大壮在外打工，我们去找他。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300072] = {["ID"] = 300072,["Text_CN"] = "一来到美食街，真想吃一碗拉面啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300073] = {["ID"] = 300073,["Text_CN"] = "我们到处找找有没有不明团伙的踪迹吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300074] = {["ID"] = 300074,["Text_CN"] = "你真机智。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300075] = {["ID"] = 300075,["Text_CN"] = "他们往那个方向去了，我们跟上去看看。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300076] = {["ID"] = 300076,["Text_CN"] = "这几个放风的可真水啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300077] = {["ID"] = 300077,["Text_CN"] = "我们去听听他们在说什么。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300078] = {["ID"] = 300078,["Text_CN"] = "别离太近了，当心被发现。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300079] = {["ID"] = 300079,["Text_CN"] = "鱼骨组的老巢在鱼骨海滨。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300080] = {["ID"] = 300080,["Text_CN"] = "那么，祝你们一路顺风了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300081] = {["ID"] = 300081,["Text_CN"] = "这列车双霸是猴子请来的吗。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300082] = {["ID"] = 300082,["Text_CN"] = "猴子太多，我们上车顶去吧，甩掉几个笨猴子。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300083] = {["ID"] = 300083,["Text_CN"] = "终于到了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300084] = {["ID"] = 300084,["Text_CN"] = "好漂亮的大海，我们快去寻找鱼骨组的据点吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300085] = {["ID"] = 300085,["Text_CN"] = "龙川的土方来这里的目的是什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300086] = {["ID"] = 300086,["Text_CN"] = "难道龙川和鱼骨组有关，走，去找土方问个清楚！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300087] = {["ID"] = 300087,["Text_CN"] = "阿恺对摄影是真爱。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300088] = {["ID"] = 300088,["Text_CN"] = "风铃森林就在前面的山上，你们去吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300089] = {["ID"] = 300089,["Text_CN"] = "那个木桶是做什么的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300090] = {["ID"] = 300090,["Text_CN"] = "该不会林浩在木桶里吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300091] = {["ID"] = 300091,["Text_CN"] = "山上的原住民开始攻击木桶了，先阻止他们再说！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300092] = {["ID"] = 300092,["Text_CN"] = "抱歉没能帮上你们。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300093] = {["ID"] = 300093,["Text_CN"] = "鱼骨组的人刚离开不久，你们现在下山，说不定能遇上他们。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300094] = {["ID"] = 300094,["Text_CN"] = "多谢你，青木，我会再来看你的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300095] = {["ID"] = 300095,["Text_CN"] = "运气真好，又有林浩的线索了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300096] = {["ID"] = 300096,["Text_CN"] = "不过，你刚才出手也太重了，快去看看那个沙向有没有事吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300097] = {["ID"] = 300097,["Text_CN"] = "这次远征可真够折腾的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300098] = {["ID"] = 300098,["Text_CN"] = "走吧，去东京找林浩。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300099] = {["ID"] = 300099,["Text_CN"] = "这东京的帮派可真有领土意识啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300100] = {["ID"] = 300100,["Text_CN"] = "糟了，是桃子。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300101] = {["ID"] = 300101,["Text_CN"] = "她怎么在这里？我去问问。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300102] = {["ID"] = 300102,["Text_CN"] = "我就说吧，对桃子不能掉以轻心。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300103] = {["ID"] = 300103,["Text_CN"] = "不过看桃子行事，应该和鱼骨组无关。算了，走吧，找林浩要紧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300104] = {["ID"] = 300104,["Text_CN"] = "桃子这家伙……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300105] = {["ID"] = 300105,["Text_CN"] = "看样子桃子以前没少给你们苦头吃啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300106] = {["ID"] = 300106,["Text_CN"] = "看她是个女人，让着她罢了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300107] = {["ID"] = 300107,["Text_CN"] = "你怎么知道“水门”在哪里？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300108] = {["ID"] = 300108,["Text_CN"] = "这很奇怪吗，我是东京人，有所听闻也很正常。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300109] = {["ID"] = 300109,["Text_CN"] = "你说的好有道理，我竟无言以对。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300110] = {["ID"] = 300110,["Text_CN"] = "原来刚刚的下水道才是真正的“水门”，真是不可思议啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300111] = {["ID"] = 300111,["Text_CN"] = "这鱼骨组可真够小心的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300112] = {["ID"] = 300112,["Text_CN"] = "走吧，去真正的“水门”。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300113] = {["ID"] = 300113,["Text_CN"] = "鱼骨组的人竟然在垃圾桶了藏了枪？！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300114] = {["ID"] = 300114,["Text_CN"] = "鱼骨组竟然有这么危险的东西，得加快脚步了，希望林浩不要出什么事。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300115] = {["ID"] = 300115,["Text_CN"] = "你可真任性啊，不说一声就走掉。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300116] = {["ID"] = 300116,["Text_CN"] = "哈哈哈，还不是被你们给找到了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300117] = {["ID"] = 300117,["Text_CN"] = "走吧，这次可不能让凛之助跑掉了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300118] = {["ID"] = 300118,["Text_CN"] = "奔波这么多天，终于抓住凛之助了，好累啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300119] = {["ID"] = 300119,["Text_CN"] = "你也放心吧，凛之助自首后，你哥哥很快就会放出来的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300120] = {["ID"] = 300120,["Text_CN"] = "早啊，幸野。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300121] = {["ID"] = 300121,["Text_CN"] = "现在才来上学，你迟到了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300122] = {["ID"] = 300122,["Text_CN"] = "我的床老了，需要人照顾嘛。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300123] = {["ID"] = 300123,["Text_CN"] = "那边怎么那么吵，发生了什么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300124] = {["ID"] = 300124,["Text_CN"] = "可恶，凤仙竟然趁着我们不在，占领了铃兰！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300125] = {["ID"] = 300125,["Text_CN"] = "刚才听人报信说，桃子也来了，在楼道里，走，去看看。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300126] = {["ID"] = 300126,["Text_CN"] = "凤仙的人太嚣张了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300127] = {["ID"] = 300127,["Text_CN"] = "刚才你们317班传出了巨大的声音。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300128] = {["ID"] = 300128,["Text_CN"] = "敢在317闹事？真不把我这317老大放在眼里！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300129] = {["ID"] = 300129,["Text_CN"] = "兵分两路，我这就去布置路障。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300130] = {["ID"] = 300130,["Text_CN"] = "那么，武田卫就交给你了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300131] = {["ID"] = 300131,["Text_CN"] = "我的任务已经完成了，哈哈哈。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300132] = {["ID"] = 300132,["Text_CN"] = "什么意思？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300133] = {["ID"] = 300133,["Text_CN"] = "我的任务就是引你来这里，哈哈哈哈哈。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300134] = {["ID"] = 300134,["Text_CN"] = "糟了，得快去学校门口看看！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300135] = {["ID"] = 300135,["Text_CN"] = "幸好你及时赶到。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300136] = {["ID"] = 300136,["Text_CN"] = "情况如何了？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300137] = {["ID"] = 300137,["Text_CN"] = "凤仙果然开了车过来，他们正在损坏我们布置的路障！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300138] = {["ID"] = 300138,["Text_CN"] = "总算是拦住了凤仙的车。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300139] = {["ID"] = 300139,["Text_CN"] = "刚才有人看见松北领着一队人去了篮球馆，这里有我，你快去看看吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300140] = {["ID"] = 300140,["Text_CN"] = "竟然想到从篮球馆攻入铃兰，松北的头脑果然厉害。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300141] = {["ID"] = 300141,["Text_CN"] = "关于隼人之死，松北对铃兰有误会，你去和他解释一下吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[300142] = {["ID"] = 300142,["Text_CN"] = "真是的，浪费我的时间。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1001] = {["ID"] = 1001,["Text_CN"] = "恺哥今天怎么一脸丧气样，拉面里被人放多了盐吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1002] = {["ID"] = 1002,["Text_CN"] = "（冰冷的瞪了健太一眼，又荡漾出一副阴险的笑脸来）",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1003] = {["ID"] = 1003,["Text_CN"] = "今天新来了一个弱不禁风的家伙，好像还很有钱哦~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1004] = {["ID"] = 1004,["Text_CN"] = "可是已经这个时候，不会已经被人抢了先吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1005] = {["ID"] = 1005,["Text_CN"] = "这种好事，我都是只告诉你健太啊，那人刚进校门，马上就要踏进教学楼了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1006] = {["ID"] = 1006,["Text_CN"] = "果然够意思，晚上拉面我请了！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1007] = {["ID"] = 1007,["Text_CN"] = "大！大哥！你！你怎！怎么样！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1008] = {["ID"] = 1008,["Text_CN"] = "喂，我说你们没长眼睛，看不到他晕了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1009] = {["ID"] = 1009,["Text_CN"] = "你们放的这是什么烟，怎么味道如此浮夸……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1010] = {["ID"] = 1010,["Text_CN"] = "大哥……大哥他不会……又被自己的……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1011] = {["ID"] = 1011,["Text_CN"] = "虽说都是些毫无存在感的家伙，但……竟觉得有些有气无力，这，不合理啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1012] = {["ID"] = 1012,["Text_CN"] = "难道是……早上那个手抓饼……果然味道不正宗啊……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1013] = {["ID"] = 1013,["Text_CN"] = "这里是传说中的乌鸦学校，可不要低估大家的实力。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1014] = {["ID"] = 1014,["Text_CN"] = "难道说我的实力也仅此而已？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1015] = {["ID"] = 1015,["Text_CN"] = "不要灰心，每个人的实力都是在不懈的努力中变得更强。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1016] = {["ID"] = 1016,["Text_CN"] = "希望老师能帮助我，登上铃兰的巅峰。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1017] = {["ID"] = 1017,["Text_CN"] = "没问题，跟我来~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1018] = {["ID"] = 1018,["Text_CN"] = "实力的提升，是一个渐进的过程，不能着急，一步一步脚踏实地的前进。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1019] = {["ID"] = 1019,["Text_CN"] = "这里人这么多，跟着他们走就可以了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1020] = {["ID"] = 1020,["Text_CN"] = "当然，不可以！这里提高实力的方法有很多，我们先从装备强化学起。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1021] = {["ID"] = 1021,["Text_CN"] = "装备……强化……难道让我学缝衣服……？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1022] = {["ID"] = 1022,["Text_CN"] = "在这里，你可以对身上的装备进行强化、镶嵌和洗练。首先，我先教你如何对装备进行强化。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1023] = {["ID"] = 1023,["Text_CN"] = "需要先练习什么缝纫等级之类坑爹的生活技能吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1024] = {["ID"] = 1024,["Text_CN"] = "当然不用，强化要用的是装备强化石，强化到高等级后，还需要相应种类的强化书。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1025] = {["ID"] = 1025,["Text_CN"] = "就这些？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1026] = {["ID"] = 1026,["Text_CN"] = "以及，一点，小小的代工费~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1027] = {["ID"] = 1027,["Text_CN"] = "下面按照我说的，强化一件装备试试看~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1028] = {["ID"] = 1028,["Text_CN"] = "报告老师，所有装备，强化完毕！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1029] = {["ID"] = 1029,["Text_CN"] = "强化装备时，装备的最高等级和学员的等级相同，所以每当学员升级时，都要记得来强化装备哦~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1030] = {["ID"] = 1030,["Text_CN"] = "哈哈哈，现在感觉自己充满了力量！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1031] = {["ID"] = 1031,["Text_CN"] = "哥几个，给我把刚才那个家伙找出来，决不能饶了他！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1032] = {["ID"] = 1032,["Text_CN"] = "是！老大！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1033] = {["ID"] = 1033,["Text_CN"] = "啊，要上课了，我得赶快进教室去……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1034] = {["ID"] = 1034,["Text_CN"] = "老师的实力，也就……还可以嘛~哈哈哈哈",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1035] = {["ID"] = 1035,["Text_CN"] = "天天拿着根鞭子，吓唬谁呦~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1036] = {["ID"] = 1036,["Text_CN"] = "虽然力量是增强了，但还是笨手笨脚的，这样可撑不过下一个挑战哦。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1037] = {["ID"] = 1037,["Text_CN"] = "提高了装备这些硬件，接着要改善下你的软件。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1038] = {["ID"] = 1038,["Text_CN"] = "已做好准备，来给我洗脑吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1039] = {["ID"] = 1039,["Text_CN"] = "请，正经一点。我们来很严肃的聊一聊，你战斗时使用的技能。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1040] = {["ID"] = 1040,["Text_CN"] = "每位学员的技能分为三系，基础攻击、主动释放技和被动触发技，现在我们先来学习如何提高主动技能等级。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1041] = {["ID"] = 1041,["Text_CN"] = "真是心潮澎湃，现在的我是不是已经身轻如燕百步穿杨了？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1042] = {["ID"] = 1042,["Text_CN"] = "快醒醒，你是学生，不是忍者。后面还有很多的挑战，做好准备了么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1043] = {["ID"] = 1043,["Text_CN"] = "迫不及待要伸展伸展拳脚！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1044] = {["ID"] = 1044,["Text_CN"] = "喂，你们几个，都准备好了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1045] = {["ID"] = 1045,["Text_CN"] = "还，还差，差几根棍子！我已经让兄弟们去找了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1046] = {["ID"] = 1046,["Text_CN"] = "没用，棍子这种应该随身携带的东西都没有，以后怎么跟我们老大混！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1047] = {["ID"] = 1047,["Text_CN"] = "我们，我们不会让老，老大失望！一定让那，那个家伙知道咱们的厉害！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1048] = {["ID"] = 1048,["Text_CN"] = "你！你听着！今天兄弟我状态不好，下，下次再找你算账！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1049] = {["ID"] = 1049,["Text_CN"] = "拳脚不行，废话倒是特别多。我！我听！听见了！下次吃饱了再来~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1050] = {["ID"] = 1050,["Text_CN"] = "看起来，你在这里混的风生水起啊。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1051] = {["ID"] = 1051,["Text_CN"] = "应付这些人，根本没有难度嘛，只是被他们吵吵的耳朵嗡嗡嗡作响。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1052] = {["ID"] = 1052,["Text_CN"] = "那边是通向天台的楼梯吗？看上去很清静的样子，我去溜达溜达~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1053] = {["ID"] = 1053,["Text_CN"] = "天台上感觉如何？很清静吧？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1054] = {["ID"] = 1054,["Text_CN"] = "这个地方，混乱果然占据了每个角落，一处安静的地方都没有。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1055] = {["ID"] = 1055,["Text_CN"] = "乌鸦学校，就像一个乱世，你需要的不是龟缩在角落，而是和你并肩作战的朋友。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1056] = {["ID"] = 1056,["Text_CN"] = "朋友？刚交了一个~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1057] = {["ID"] = 1057,["Text_CN"] = "嗯……比那种朋友……更进一步……你还是跟我来吧……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1058] = {["ID"] = 1058,["Text_CN"] = "交朋友是吧？难道是让我在大街上跟人搭讪吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1059] = {["ID"] = 1059,["Text_CN"] = "怎么会那么浮夸，乌鸦高校有专门招募朋友扩充战队的地方。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1060] = {["ID"] = 1060,["Text_CN"] = "喂，你说的这个才浮夸好吧，简直就是个征兵系统。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1061] = {["ID"] = 1061,["Text_CN"] = "看到上面的招募按钮了吗？每隔一段时间，就会有一次免费的招募机会，想要壮大自己的组织，就去那里吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1062] = {["ID"] = 1062,["Text_CN"] = "来见见你的老朋友。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1063] = {["ID"] = 1063,["Text_CN"] = "刚才在天台，表现的很不错哦~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1064] = {["ID"] = 1064,["Text_CN"] = "等等！我们不是认识过了吗？为什么好像又要交一次朋友的样子？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1065] = {["ID"] = 1065,["Text_CN"] = "今后，兰同学就是你组织中的一员了，你们会并肩战斗，同进共退。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1066] = {["ID"] = 1066,["Text_CN"] = "虽然事情来的好突然，但感觉像是对我实力的一种肯定！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1067] = {["ID"] = 1067,["Text_CN"] = "好好表现，千万别让我为自己的选择失望~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1068] = {["ID"] = 1068,["Text_CN"] = "招募到的新的学员，首先要做的，就是进入学员界面，为学员提升等级。我准备了许多好吃的经验寿司哦~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1069] = {["ID"] = 1069,["Text_CN"] = "你在天台从凉介手中救下一郎之后，他们的老大已经吩咐手下所有人针对你展开行动。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1070] = {["ID"] = 1070,["Text_CN"] = "行动？为什么听起来如此诡异……直接说要揍我多直观。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1071] = {["ID"] = 1071,["Text_CN"] = "看，他们朝这边走来了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1072] = {["ID"] = 1072,["Text_CN"] = "第一天上课就引来这么多是非，好头疼，我要去打个篮球舒缓下精神。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1073] = {["ID"] = 1073,["Text_CN"] = "我陪你一起去！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1074] = {["ID"] = 1074,["Text_CN"] = "等一下，现在有一件非常重要的事要做，跟我来吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1075] = {["ID"] = 1075,["Text_CN"] = "对学员自身的培养，除了提升等级外，还有进化和进阶两种方式。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1076] = {["ID"] = 1076,["Text_CN"] = "进化需要消耗相应学员的碎片，使用足够数量的碎片，就可以为学员的品质增加一颗星，实力会大幅度提高。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1077] = {["ID"] = 1077,["Text_CN"] = "当任何一位学员的品质达到2星时，会开启大招释放技能，在战场上，这可是发挥着决定全场胜负的关键所在。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1078] = {["ID"] = 1078,["Text_CN"] = "铃兰之巅，这下志在必得！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1079] = {["ID"] = 1079,["Text_CN"] = "低调，要低调……快用心领悟你终极大招的精髓吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1080] = {["ID"] = 1080,["Text_CN"] = "随按说学习到了传说中非常厉害的终极大招，但，并没有什么神清气爽的感觉。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1081] = {["ID"] = 1081,["Text_CN"] = "学习招式，又不是升华灵魂，你要求的太多了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1082] = {["ID"] = 1082,["Text_CN"] = "接下来，自己尝试将这些技能升级吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1083] = {["ID"] = 1083,["Text_CN"] = "兰同学，你篮球打得好么？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1084] = {["ID"] = 1084,["Text_CN"] = "从小到大都是校队主力，拉拉队。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1085] = {["ID"] = 1085,["Text_CN"] = "那一定看过不少激烈的比赛，今天看看我的水平如何。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1086] = {["ID"] = 1086,["Text_CN"] = "咦，你们也去打球了，看到小柳了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1087] = {["ID"] = 1087,["Text_CN"] = "看到了，他在……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1088] = {["ID"] = 1088,["Text_CN"] = "他刚进去，我们就出来了，老师你要找小柳吗？我们还有事，先走了~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1089] = {["ID"] = 1089,["Text_CN"] = "好的，我进去找他。你们路上注意安全~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1090] = {["ID"] = 1090,["Text_CN"] = "为什么不让我说话？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1091] = {["ID"] = 1091,["Text_CN"] = "她要是知道你把小柳打成重伤，你就惨了，别冷着，快离开这里吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1092] = {["ID"] = 1092,["Text_CN"] = "喂！你不会遇到真子老师了吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1093] = {["ID"] = 1093,["Text_CN"] = "是啊，就在那边街角。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1094] = {["ID"] = 1094,["Text_CN"] = "快让我好好看看，有没有受伤……怎么，好像一点事都没有的样子。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1095] = {["ID"] = 1095,["Text_CN"] = "老师并没有你想的那么可怕，她其实内心还是个温柔可人的萌妹子的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1096] = {["ID"] = 1096,["Text_CN"] = "对了，有一个朋友听说你的大名，想要加入我们，我带你去见见他~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1097] = {["ID"] = 1097,["Text_CN"] = "那，这位就是土方同学，有一辆非常炫酷的机车哦！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1098] = {["ID"] = 1098,["Text_CN"] = "土方你好，我喜欢你的T恤~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1099] = {["ID"] = 1099,["Text_CN"] = "……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1100] = {["ID"] = 1100,["Text_CN"] = "土方的意思呢，是有空骑车带你出去兜风拉~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1101] = {["ID"] = 1101,["Text_CN"] = "……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1102] = {["ID"] = 1102,["Text_CN"] = "土方说以后我们要同心协力，团结一致，互帮互助，相亲相爱……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1103] = {["ID"] = 1103,["Text_CN"] = "喂，话怎么都被你说了，土方兄可是一个字都没说呢。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1104] = {["ID"] = 1104,["Text_CN"] = "不要纠结这些细节啦，领会精神就好。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1105] = {["ID"] = 1105,["Text_CN"] = "这么快就升到了10级，可喜可贺。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1106] = {["ID"] = 1106,["Text_CN"] = "一路过来，见神杀神见佛杀佛，可真是……还挺无趣的……这里的学生是怎么熬过来的？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1107] = {["ID"] = 1107,["Text_CN"] = "现在带你去一个地方，学校中最为斗志激昂之所在，武道馆。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1108] = {["ID"] = 1108,["Text_CN"] = "所有的玩家都可以在这里通过自己的实力，赢得在铃兰属于自己的地位。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1109] = {["ID"] = 1109,["Text_CN"] = "依照每天所排的名次，会获得相应的奖励，带着奖励可以在售卖武道良品的商店里换取许多好东西。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1110] = {["ID"] = 1110,["Text_CN"] = "进入武道馆要做的第一件事，就是设置好自己的防守阵容。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1111] = {["ID"] = 1111,["Text_CN"] = "第一次进入的时候，我们会为你安排好三个学员作为防守阵容，以后随着组织学员数量的更多更强，还需要你自己去调整。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1112] = {["ID"] = 1112,["Text_CN"] = "防守阵容的三个位置，分别对应武道馆战斗中的三场1v1战斗，请尽量安排独自作战能力最强的学员在这里。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1113] = {["ID"] = 1113,["Text_CN"] = "只有实力雄厚的防守阵容，才能使自己辛苦赢得的成果不被别人夺走，千万要记着哦~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1114] = {["ID"] = 1114,["Text_CN"] = "设置好了防守阵容，接下来就可以进行第一次挑战了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[1115] = {["ID"] = 1115,["Text_CN"] = "每次会显示适合你挑战的四个玩家，查看他们的战力和英雄，选择有把握的开始挑战吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2001] = {["ID"] = 2001,["Text_CN"] = "控制左下角的#16ff04摇杆#进行移动",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2002] = {["ID"] = 2002,["Text_CN"] = "继续前进，走到#16ff04光圈位置#",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2003] = {["ID"] = 2003,["Text_CN"] = "点击#16ff04普攻#按钮释放#16ff04普通攻击#，长按连续释放",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2004] = {["ID"] = 2004,["Text_CN"] = "释放技能#16ff04流星落#，腾空后请保持在敌人#16ff04正上方#",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2005] = {["ID"] = 2005,["Text_CN"] = "释放技能#16ff04异界空间#，请保证敌人所处位置在自己的#16ff04正前方#",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2100] = {["ID"] = 2100,["Text_CN"] = "继续跟上，走到前方的#16ff04光圈#里。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2101] = {["ID"] = 2101,["Text_CN"] = "走得不错，步法飘逸，下面教授你基本战斗技巧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2102] = {["ID"] = 2102,["Text_CN"] = "请正面#16ff04近距离#靠近敌人，然后点按攻击按钮，对敌人进行攻击。#16ff04按住不放#可进行#16ff04连续攻击#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2103] = {["ID"] = 2103,["Text_CN"] = "走得不错，步法飘逸，下面教授你基本战斗技巧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2104] = {["ID"] = 2104,["Text_CN"] = "请与敌人#16ff04保持一段距离#，然后点按攻击按钮，对敌人射出箭矢。#16ff04按住不放#可进行#16ff04连续攻击#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2105] = {["ID"] = 2105,["Text_CN"] = "我的天，只是试一试身手便打得落花流水，看那边，又围过来好多人！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2106] = {["ID"] = 2106,["Text_CN"] = "别慌，这就帮你开启#16ff04新技能#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2107] = {["ID"] = 2107,["Text_CN"] = "发动技能#16ff04流星落#，可以腾空飞起，参照#16ff04地面的准圈#移动到#16ff04敌人头顶#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2108] = {["ID"] = 2108,["Text_CN"] = "#16ff04落地后#会对周围造成#16ff04大量伤害#，所以一定要控制好#16ff04移动的位置#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2109] = {["ID"] = 2109,["Text_CN"] = "发动技能#16ff04异界空间#，将向正前方#16ff04射出一箭#，#16ff04吸附住#附近的敌人。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2110] = {["ID"] = 2110,["Text_CN"] = "注意一定要#16ff04保持#和敌人有#16ff04一段距离#，才能最有效的发挥技能的威力。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2111] = {["ID"] = 2111,["Text_CN"] = "新技能#16ff04魂之力#，释放后在#16ff04身体周围#卷起魂之气息，#16ff04随后爆炸#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2112] = {["ID"] = 2112,["Text_CN"] = "释放时请保持和敌人#16ff04较近距离#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2113] = {["ID"] = 2113,["Text_CN"] = "新技能#16ff04幻箭雨#，释放后向前方上空射出箭雨并#16ff04散落在前方地面#，造成大范围伤害。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2114] = {["ID"] = 2114,["Text_CN"] = "释放时请注意#16ff04和敌人保持恰当距离#。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2115] = {["ID"] = 2115,["Text_CN"] = "#16ff04车轮对战#关卡中，#16ff04敌人#会#16ff04逐一上场#进行挑战，前一位落败后下一位才会出现。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2116] = {["ID"] = 2116,["Text_CN"] = "虽然#16ff04同时对抗#的敌人#16ff04数量少#了，但是每一位都#16ff04实力强大#，请小心应付。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2117] = {["ID"] = 2117,["Text_CN"] = "*N_B的职业技能是#16ff04向前方瞬移#，使用此招可以进行#16ff04快速移动#追上敌人或者避开敌人攻击。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[2118] = {["ID"] = 2118,["Text_CN"] = "每位学员依照各自的#16ff04不同职业#，#16ff04此技能#也有所#16ff04区分#，今后可以慢慢体验。",},
	[2119] = {["ID"] = 2119,["Text_CN"] = "#16ff04绝对守护#关卡中，你需要#16ff04保护目标人物或道具#的安全，并在#16ff04规定时间内击退敌人#。",},
	[2120] = {["ID"] = 2120,["Text_CN"] = "自本关起，你可以开启终极奥义#16ff04必杀技#了。",},
	[2121] = {["ID"] = 2121,["Text_CN"] = "必杀技#16ff04黑魂·释#释放后会#16ff04抓取周围所有目标#至#16ff04浮空#，并#16ff04召唤出魂刺#从天而降，造成#16ff04大量伤害#。",},
	[2122] = {["ID"] = 2122,["Text_CN"] = "必杀#16ff04咒·封印箭#释放后会跃至空中#16ff04向下射出#只戾气极重的#16ff04灵魂箭#，接触地面后发生#16ff04爆炸#，造成#16ff04大量伤害#。",},
	[3001] = {["ID"] = 3001,["Text_CN"] = "哇，这里就是传说中的乌鸦学校！不敢相信能够站在这里。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3002] = {["ID"] = 3002,["Text_CN"] = "新同学，别傻站着，快过来领新生证。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3003] = {["ID"] = 3003,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3004] = {["ID"] = 3004,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3005] = {["ID"] = 3005,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3006] = {["ID"] = 3006,["Text_CN"] = "领取了新生证，就正式成为我们学校的一员了，下面，请允许我一一向你介绍在这里生活的规则。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3007] = {["ID"] = 3007,["Text_CN"] = "首先，要靠自己的实力，让大家认识你。这个过程，就从校门口的冒险开始吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3008] = {["ID"] = 3008,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3009] = {["ID"] = 3009,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3010] = {["ID"] = 3010,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3011] = {["ID"] = 3011,["Text_CN"] = "顶着小鲜肉的头衔到处走，会不会显得太高调……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3012] = {["ID"] = 3012,["Text_CN"] = "喂！那边的！小鲜……小鲜肉是什么鬼？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3013] = {["ID"] = 3013,["Text_CN"] = "果然引起了周遭群众的不满，可是，这个聒噪的家伙又是谁？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3014] = {["ID"] = 3014,["Text_CN"] = "他是龙川高中的阿恺，外号透明人，今天竟然出现在这里，来者不善，你要小心应付。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3015] = {["ID"] = 3015,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3016] = {["ID"] = 3016,["Text_CN"] = "恺哥怎么一脸嫌弃，又有不知深浅的家伙惹事了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3017] = {["ID"] = 3017,["Text_CN"] = "啊，刚来的新生不知规矩，略微教育了一番。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3018] = {["ID"] = 3018,["Text_CN"] = "咦，是那边走过的家伙么？我也去围观一下。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3019] = {["ID"] = 3019,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3020] = {["ID"] = 3020,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3021] = {["ID"] = 3021,["Text_CN"] = "大！大哥！你！你怎！怎么样！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3022] = {["ID"] = 3022,["Text_CN"] = "你们放的这是什么烟……如此浮夸……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3023] = {["ID"] = 3023,["Text_CN"] = "大哥……大哥他不会……又被自己的……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3024] = {["ID"] = 3024,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3025] = {["ID"] = 3025,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3026] = {["ID"] = 3026,["Text_CN"] = "只要努力获得满星通关，就能领取所有的章节奖励。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3027] = {["ID"] = 3027,["Text_CN"] = "奖励的内容看上去还不错呢。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3028] = {["ID"] = 3028,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3029] = {["ID"] = 3029,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3030] = {["ID"] = 3030,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3031] = {["ID"] = 3031,["Text_CN"] = "升级了技能，感觉自己变得更加强大了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3032] = {["ID"] = 3032,["Text_CN"] = "快去开启新的冒险吧~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3033] = {["ID"] = 3033,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3034] = {["ID"] = 3034,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3035] = {["ID"] = 3035,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3036] = {["ID"] = 3036,["Text_CN"] = "喂！你这家伙！不要跑，等我歇会儿回过神了再好好教训你！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3037] = {["ID"] = 3037,["Text_CN"] = "喂！我们老大再叫你！听见了吗！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3038] = {["ID"] = 3038,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3039] = {["ID"] = 3039,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3040] = {["ID"] = 3040,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3041] = {["ID"] = 3041,["Text_CN"] = "当学员升级之后，学员的装备也需要强化到相同等级。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3042] = {["ID"] = 3042,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3043] = {["ID"] = 3043,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3044] = {["ID"] = 3044,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3045] = {["ID"] = 3045,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3046] = {["ID"] = 3046,["Text_CN"] = "强化过装备后，实力也得到了很大的提升，请继续前往冒险一试身手。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3047] = {["ID"] = 3047,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3048] = {["ID"] = 3048,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3049] = {["ID"] = 3049,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3050] = {["ID"] = 3050,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3051] = {["ID"] = 3051,["Text_CN"] = "嘿，我的朋友们。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3052] = {["ID"] = 3052,["Text_CN"] = "这位是原叶君，316班的老大。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3053] = {["ID"] = 3053,["Text_CN"] = "嘿，朋友，今天起让我们一起为这所学校的荣耀而奋斗吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3054] = {["ID"] = 3054,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3055] = {["ID"] = 3055,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3056] = {["ID"] = 3056,["Text_CN"] = "激活天赋需要消耗天赋点，学员每升1品时可以获得2天赋点。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3057] = {["ID"] = 3057,["Text_CN"] = "每一排的多个天赋选项，只能选择一个进行升级，选择操作后可以消耗钻石进行重置。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3058] = {["ID"] = 3058,["Text_CN"] = "天赋由初始的第一排依次向下激活，每一排升级至上限后可以激活下一排。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3059] = {["ID"] = 3059,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3060] = {["ID"] = 3060,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3061] = {["ID"] = 3061,["Text_CN"] = "在这个次元中，每个人都有自己的属性类型，他们分为格斗家、柔术家和功夫家三大类。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3062] = {["ID"] = 3062,["Text_CN"] = "属性克制，比如功夫家可以对柔术家造成更大伤害量，柔术家对格斗家、格斗家对功夫家雷同。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3063] = {["ID"] = 3063,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3064] = {["ID"] = 3064,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3065] = {["ID"] = 3065,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3066] = {["ID"] = 3066,["Text_CN"] = "请问，我可以一起参加战斗了吗？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3067] = {["ID"] = 3067,["Text_CN"] = "这就安排你上场，好好发挥别让他们小瞧了~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3068] = {["ID"] = 3068,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3069] = {["ID"] = 3069,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3070] = {["ID"] = 3070,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3071] = {["ID"] = 3071,["Text_CN"] = "完成相应的成就任务，可以获得丰厚奖励，前进的步伐，不要停止哦~",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3072] = {["ID"] = 3072,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3073] = {["ID"] = 3073,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3074] = {["ID"] = 3074,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3075] = {["ID"] = 3075,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3076] = {["ID"] = 3076,["Text_CN"] = "获得新学员宫野兰，似乎激活了什么不得了的缘分。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3077] = {["ID"] = 3077,["Text_CN"] = "咦，原叶你竟然也在这里！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3078] = {["ID"] = 3078,["Text_CN"] = "这……你并没有告诉我你要来……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3079] = {["ID"] = 3079,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3080] = {["ID"] = 3080,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3081] = {["ID"] = 3081,["Text_CN"] = "每位学员的缘分系统中，都有特定的缘分搭配，获得其中相应的学员可以激活该缘分。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3082] = {["ID"] = 3082,["Text_CN"] = "缘分激活后可提升战斗力，学员升品和升星都将提升缘分等级，等级越高，提升的战斗力越多。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3083] = {["ID"] = 3083,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3084] = {["ID"] = 3084,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3085] = {["ID"] = 3085,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3086] = {["ID"] = 3086,["Text_CN"] = "进入武道馆首先要做的，就是设置防守阵容。随着组织学员更多更强，还需要你及时进行调整。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3087] = {["ID"] = 3087,["Text_CN"] = "只有防守阵容足够强大，才能使自己辛苦赢得的成果不被别人夺走。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3088] = {["ID"] = 3088,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3089] = {["ID"] = 3089,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3090] = {["ID"] = 3090,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3091] = {["ID"] = 3091,["Text_CN"] = "接着，就是大显身手的时候，参照武道馆的排名和对手的战力，努力证明自己的存在吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3092] = {["ID"] = 3092,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3093] = {["ID"] = 3093,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3094] = {["ID"] = 3094,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3095] = {["ID"] = 3095,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3096] = {["ID"] = 3096,["Text_CN"] = "当你在武道馆获取排名后，每小时根据你的实时排名发放奖励，维持的排名越高，奖励越丰厚。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3097] = {["ID"] = 3097,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3098] = {["ID"] = 3098,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3099] = {["ID"] = 3099,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3100] = {["ID"] = 3100,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3101] = {["ID"] = 3101,["Text_CN"] = "这里每天都会提供一定数量的日常任务，完成每一个任务都可获得相应的活跃度。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
