	[3111] = {["ID"] = 3111,["Text_CN"] = "随装备等级的提升，会逐条开启最多四项装备属性洗练，洗练结果分为白绿蓝紫橙五档。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3112] = {["ID"] = 3112,["Text_CN"] = "当其中某条洗出高品级属性时，可以将其锁定，再洗练其他条目。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
