	[3116] = {["ID"] = 3116,["Text_CN"] = "在不懈努力下，终于能够加入公会与更多朋友并肩奋斗了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3117] = {["ID"] = 3117,["Text_CN"] = "这里有一些公会的推荐，你可以寻找合适的公会提交加入申请，但是请注意同时申请的公会不能超过3个。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3118] = {["ID"] = 3118,["Text_CN"] = "也可以创建属于自己的公会，聚集起和自己志同道合的朋友。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3119] = {["ID"] = 3119,},
	[3120] = {["ID"] = 3120,},
	[3121] = {["ID"] = 3121,["Text_CN"] = "放课后的美好时光，有三种不同模式的挑战，需要采用不同的作战阵容才能顺利通关。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3122] = {["ID"] = 3122,},
