	[3125] = {["ID"] = 3125,},
	[3126] = {["ID"] = 3126,["Text_CN"] = "每种模式的战斗，都有适合不同阶段战力的难度可以选择。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3127] = {["ID"] = 3127,["Text_CN"] = "请对比各难度的推荐战力和自己的上阵战力，选择收益最高的进行挑战哦。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3128] = {["ID"] = 3128,},
	[3129] = {["ID"] = 3129,},
	[3130] = {["ID"] = 3130,},
	[3131] = {["ID"] = 3131,["Text_CN"] = "用实力战胜迎面而来的挑战，一层层向上攀登，成为学校的主宰。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3132] = {["ID"] = 3132,["Text_CN"] = "请注意，每天都有2次重置快速通关的机会，诸多奖励千万不要错过。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3133] = {["ID"] = 3133,},
	[3134] = {["ID"] = 3134,},
	[3135] = {["ID"] = 3135,},
	[3136] = {["ID"] = 3136,["Text_CN"] = "那速度风驰电掣，那绚丽全场瞩目，想拥有最新奇的座驾，来这里绝对没错。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3137] = {["ID"] = 3137,["Text_CN"] = "拥有坐骑后，不仅可以在街道上向别人展示，还能获得战力提升。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3138] = {["ID"] = 3138,},
	[3139] = {["ID"] = 3139,},
	[3140] = {["ID"] = 3140,},
	[3141] = {["ID"] = 3141,["Text_CN"] = "万众瞩目的S级学员，每天都有其中一位在这里等着大家的挑战。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3142] = {["ID"] = 3142,["Text_CN"] = "所有挑战者所造成的伤害量每天都会进行排名，然后根据排名的高低发放奖励。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3143] = {["ID"] = 3143,},
	[3144] = {["ID"] = 3144,},
	[3145] = {["ID"] = 3145,},
	[3146] = {["ID"] = 3146,["Text_CN"] = "游乐场被人霸占，曾经那些惊奇的历险都成为私人所有，请帮大家把游乐园夺回来。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3147] = {["ID"] = 3147,["Text_CN"] = "场景中共有15个挑战点，难度随关卡的开放而增大，只靠应拳头是不够的，有时还要耍点手段。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3148] = {["ID"] = 3148,},
	[3149] = {["ID"] = 3149,},
	[3150] = {["ID"] = 3150,},
	[3151] = {["ID"] = 3151,["Text_CN"] = "明白了规则，开始游乐园夺回之战吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3152] = {["ID"] = 3152,},
	[3153] = {["ID"] = 3153,},
	[3154] = {["ID"] = 3154,},
	[3155] = {["ID"] = 3155,},
	[3156] = {["ID"] = 3156,["Text_CN"] = "制霸之战是能够和其他玩家实时竞技的场所，分为1v1和3v3两场竞技模式。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3157] = {["ID"] = 3157,["Text_CN"] = "1v1由自己上阵三位学员，匹配另一位对手的三位学员进行战斗。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3158] = {["ID"] = 3158,["Text_CN"] = "3v3则只由自己上阵一位学员，再匹配出两位队友和三位对手进行战斗。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3159] = {["ID"] = 3159,},
	[3160] = {["ID"] = 3160,},
	[3161] = {["ID"] = 3161,["Text_CN"] = "左侧三个是己方上阵的三个学员位置，右侧是等待匹配的敌方阵容。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3162] = {["ID"] = 3162,["Text_CN"] = "选择完成己方阵容后，点击匹配按钮，可开启匹配。匹配开始60秒后，若没有匹配到对手，自动取消。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3163] = {["ID"] = 3163,},
	[3164] = {["ID"] = 3164,},
	[3165] = {["ID"] = 3165,},
	[3166] = {["ID"] = 3166,["Text_CN"] = "维护和平，招纳粉丝，只靠拳脚是不够的，一把拉风的武器简直事半功倍。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3167] = {["ID"] = 3167,["Text_CN"] = "这里有许多不同类型及功能的枪械可供你选择，以及，请记住，能力越大，责任越大。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3168] = {["ID"] = 3168,},
	[3169] = {["ID"] = 3169,},
	[3170] = {["ID"] = 3170,},
	[3171] = {["ID"] = 3171,["Text_CN"] = "激活徽章所消耗的是通关冒险获得的星星，在冒险的路上，不要停止，走得越远，能力越强。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3172] = {["ID"] = 3172,["Text_CN"] = "徽章系统中，有许多宝石镶嵌孔，当有新的宝石时，请及时镶嵌以提升战力。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3173] = {["ID"] = 3173,},
	[3174] = {["ID"] = 3174,},
	[3175] = {["ID"] = 3175,},
	[3176] = {["ID"] = 3176,["Text_CN"] = "走廊的尽头有一个储物柜，据说每天都有人在柜子中放置神秘任务的悬赏，完成任务便能得到丰厚奖励。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3177] = {["ID"] = 3177,["Text_CN"] = "任务的奖励有多种，根据自己的需求选择合适的任务，能很好地帮助自己达到目标。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3178] = {["ID"] = 3178,["Text_CN"] = "做任务的过程中，经常会被其他人干扰，尽量派遣实力强进的学员进行任务，减少损失。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3179] = {["ID"] = 3179,},
	[3180] = {["ID"] = 3180,},
	[3181] = {["ID"] = 3181,["Text_CN"] = "浩瀚无垠的夜空，一十二宫，二十八宿，传说中他们拥有神秘的力量，可以使我们变得更强。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3182] = {["ID"] = 3182,["Text_CN"] = "占卜星运，获得等级更高品级更稀有的星座，自己的实力也能获得更高提升。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[3183] = {["ID"] = 3183,},
	[3184] = {["ID"] = 3184,},
	[3185] = {["ID"] = 3185,},
	[4000] = {["ID"] = 4000,["Text_CN"] = "今天我们来做个了断吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4001] = {["ID"] = 4001,["Text_CN"] = "旭日高中的辣鸡，谁怕谁。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4002] = {["ID"] = 4002,["Text_CN"] = "让你知道我们龙川高中不是好惹的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4003] = {["ID"] = 4003,["Text_CN"] = "薰……今天不论如何，跟我回去吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4004] = {["ID"] = 4004,["Text_CN"] = "住口……我是绝对不会原谅你的！！！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4005] = {["ID"] = 4005,["Text_CN"] = "你们几个，有本事都冲我来。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4006] = {["ID"] = 4006,["Text_CN"] = "就这点本事，看我的。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4007] = {["ID"] = 4007,["Text_CN"] = "让你们见识一下厉害。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4008] = {["ID"] = 4008,["Text_CN"] = "薰，快住手。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4009] = {["ID"] = 4009,["Text_CN"] = "啊……烦死了！！！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4010] = {["ID"] = 4010,["Text_CN"] = "大家不要慌，靠近我。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4011] = {["ID"] = 4011,["Text_CN"] = "可恶……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4012] = {["ID"] = 4012,["Text_CN"] = "居然这么强……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4013] = {["ID"] = 4013,["Text_CN"] = "对不起……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4014] = {["ID"] = 4014,["Text_CN"] = "但是……今天我必须带你回去！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4015] = {["ID"] = 4015,["Text_CN"] = "……为、为什么。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4016] = {["ID"] = 4016,["Text_CN"] = "你终于来了。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4017] = {["ID"] = 4017,["Text_CN"] = "宫，你没事吧？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4018] = {["ID"] = 4018,["Text_CN"] = "哼哼，都在给爷挠痒痒吗。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4019] = {["ID"] = 4019,["Text_CN"] = "好样的，松北，接下来就交给我吧！",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4020] = {["ID"] = 4020,["Text_CN"] = "你俩没事吧，我给你们包扎一下。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4021] = {["ID"] = 4021,["Text_CN"] = "哼，还叫来了帮手。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4022] = {["ID"] = 4022,["Text_CN"] = "这就是你们旭日高中的实力么？弱鸡~我们走吧。",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4023] = {["ID"] = 4023,["Text_CN"] = "等一下……",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4024] = {["ID"] = 4024,["Text_CN"] = "！！！？？？",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4025] = {["ID"] = 4025,["Text_CN"] = "开场演示对白",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4026] = {["ID"] = 4026,["Text_CN"] = "开场演示对白",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
	[4099] = {["ID"] = 4099,["Text_CN"] = "无",["Text_CNFont"] = 1,["Text_CNSize"] = 10,["Text_EN"] = "element",["Text_ENFont"] = 1,["Text_ENSize"] = 10,},
