function getDataById(key_id)
    local id_data = SpeakText[key_id]

    return id_data
end

function getArrDataByField(fieldName, fieldValue)
    local arrData = {}
    for k, v in pairs(SpeakText) do
        if v[fieldName] == fieldValue then
            arrData[#arrData+1] = v
        end
    end

    return arrData
end

function release()
    _G["DB_SpeakText"] = nil
    package.loaded["DB_SpeakText"] = nil
    package.loaded["DBSystem/DB_SpeakText"] = nil
end
